mapper.insert:
insert into  diaries (
diary_id
, email
, content
, content_notag
, emotionscore
, keyword
, created_at
)
values (
diaries_seq.nextval
, #{email}
, #{content}
, #{content_notag}
, #{emotionscore}
, #{keyword}
, #{created_at}
)

mapper.select:
select diary_id, content, to_char(created_at, 'FMyyyy-mm-dd') as created_at
from diaries
where email = #{email}
  and created_at = #{created_at}

select to_char(created_at, 'FMyyyymmdd') as created_at, emotionscore
from diaries
where email = #{username}

select count(*)
from diaries
where created_at = #{created_at}
and email = #{email}

mapper.delete:
delete diaries
where email = #{email}
and created_at = #{created_at}

mapper.select:
select count(*)
from diaries
where created_at = #{dayString} and email = #{username}

select content, to_char(created_at, 'yyyy-mm-dd') as created_at
from diaries
where created_at = #{dayString}
  and email = #{username}

select nickname
from member
where email = #{writerEmail}
